
public class IntArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] array = new int[] {514,256,712,87,44,28,41,42,74,54};
		for(int i=0;i<array.length;i++) {
			System.out.print(array[i] + "  ");
		}
		System.out.println();
		for(int i=9;i>=0;i--) {
			System.out.print(array[i] + "  ");
		}
	}

}

